CREATE DATABASE ucheb_3;
USE ucheb_3;

CREATE TABLE `stories` (
  `story_id` int PRIMARY KEY AUTO_INCREMENT,
  `story_name` varchar(30),
  `story_author` int,
  `story_text` text,
  `story_genre` int
);

CREATE TABLE `genres` (
  `genre_id` int PRIMARY KEY AUTO_INCREMENT,
  `genre_name` varchar(30)
);

CREATE TABLE `authors` (
  `author_id` int PRIMARY KEY AUTO_INCREMENT,
  `author_name` varchar(60),
  `author_birth` date,
  `author_auth` int
);

CREATE TABLE `auth` (
  `auth_id` int PRIMARY KEY AUTO_INCREMENT,
  `auth_log` varchar(20),
  `auth_pwd` text
);

ALTER TABLE `stories` ADD FOREIGN KEY (`story_genre`) REFERENCES `genres` (`genre_id`);

ALTER TABLE `stories` ADD FOREIGN KEY (`story_author`) REFERENCES `authors` (`author_id`);

ALTER TABLE `authors` ADD FOREIGN KEY (`author_auth`) REFERENCES `auth` (`auth_id`);

